mapper.insert:
  		
  			SELECT 'ACT-' || LPAD((NVL(MAX(SUBSTR(act_code, -4)),0)+1),4,'0')
			FROM com_act
  		
  		INSERT INTO com_act
  							(
  							 act_code
							,act_name
							,act_num
							,act_tel
							,act_sts
							,act_kind
  							)
  		VALUES
  				(
  				#{actCode}
  				,#{actName}
  				,#{actNum}
  				,#{actTel}
  				,#{actSts}
  				,#{actKind}
  				)

mapper.update:
  		UPDATE com_act SET
  				 act_name = #{actName}
				,act_num = #{actNum}
				,act_tel = #{actTel}
				,act_sts = #{actSts}
				,act_kind = #{actKind}
		WHERE act_code = #{actCode}
