mapper.insert:
SELECT 'BNO-' || LPAD((NVL(MAX(SUBSTR(BOM_NO, -4)),0)+1),4,'0') FROM BOM_HEADER
INSERT INTO bom_header ( bom_no, prod_code, bom_wdate, bom_yn, bom_prcs_yn
) VALUES ( #{bomNo}, #{prodCode}, sysdate, #{bomYn}, 'W2' )

mapper.update:
  		UPDATE bom_header SET
								prod_code= #{prodCode},
								bom_udate= sysdate,
								bom_yn= #{bomYn},
								bom_prcs_yn= #{bomPrcsYn}
		WHERE bom_no = #{bomNo}
