mapper.update:
  	UPDATE com_commde SET
	  							commde_code = #{commdeCode},
								commde_name = #{commdeName},
								commde_info = #{commdeInfo},
								commde_use = #{commdeUse}
			WHERE commde_no = #{commDeNo}

mapper.insert:
  	INSERT INTO com_commde
	  								(
	  								commde_no
	  								,commde_code
									,comm_code
									,commde_name
									,commde_info
									,commde_use
	  								)
	  		VALUES
			  		(
			  		 get_primarycode('commDeNo')
			  		,#{commdeCode}
	  				,#{commCode}
	  				,#{commdeName}
	  				,#{commdeInfo}
	  				,#{commdeUse}
			  		)
