mapper.insert:
  		INSERT INTO com_emp(
  			emp_code
			, emp_name
			, emp_date
			, emp_tel
			, emp_pw
			, emp_role
			
			, dept_code
			
  		) VALUES (
  			get_primarycode('empCode')
  			, #{empName}
  			, SYSDATE
  			, #{empTel}
  			, #{empPw}
  			, #{empRole}
  			
			, #{deptCode}
			
  		)

mapper.update:
  		UPDATE com_emp SET
		  				  emp_name = #{empName}
						, emp_tel = #{empTel}
						
						, emp_pw = #{empPw}
						
						, emp_role = #{empRole}
						
						, dept_code = #{deptCode}
						
		WHERE	emp_code = #{empCode}

mapper.select:
SELECT emp_code, emp_name, emp_date, emp_role, dept_code, find_cdnm('0B', emp_role) AS emp_role_name, find_cdnm('DEPT-TYPE', dept_code) AS dept_code_name, emp_tel FROM com_emp
AND emp_name LIKE '%' || #{empName} || '%'
AND dept_code = #{inputDeptList}
AND TO_CHAR(emp_date, 'YYYY-MM-DD') >=#{startDate}
AND TO_CHAR(emp_date, 'YYYY-MM-DD') <=#{endDate}
ORDER BY emp_code
