insert.foreach:
insert into com_mat(
					mat_code,
					mat_name,
					mat_unit,
					mat_std,
					mat_safe
					)
values(
		get_primarycode('matCode')
		,#{l.matName}
		,#{l.matUnit}
		,#{l.matStd}
		,#{l.matSafe}
		)

update.foreach:
UPDATE com_mat
SET mat_name = #{l.matName},
	mat_unit = #{l.matUnit},
	mat_std = #{l.matStd},
	mat_safe = #{l.matSafe}
WHERE mat_code = #{l.matCode}
