mapper.insert:
SELECT 'MOC-' || TO_CHAR(sysdate, 'rrMMdd') || LPAD(NVL(MAX(SUBSTR(mat_od_cd, -3)),0) + 1, 3, '0') FROM mat_order WHERE SUBSTR(mat_od_cd, 5, 6) = TO_CHAR(sysdate, 'rrMMdd')
BEGIN
INSERT INTO mat_order VALUES( #{matOdCd} ,#{orderList[0].empCode} ,#{orderList[0].actCode} );
INSERT INTO mat_order_de(MAT_OD_DE_CD, MAT_OD_CD, MAT_AMT, MAT_CODE, MAT_OD_ACP, MAT_TEST_YN, MAT_PRICE, MAT_OD_RQ) VALUES
(get_primaryCode('matOdDeCd'), #{matOdCd}, #{order.matAmt}, #{order.matCode}, #{order.matOdAcp}, 'N', #{order.matPrice}, #{order.matOdRq});
END;

update.foreach:
UPDATE mat_order_de
SET mat_code = #{order.matCode}, mat_amt = #{order.matAmt}, mat_od_acp = #{order.matOdAcp}, mat_price = #{order.matPrice}, mat_od_rq = #{order.matOdRq}
WHERE mat_od_de_cd = #{order.matOdDeCd}

mapper.delete:
BEGIN
	
	DELETE FROM mat_order_de
	WHERE mat_od_de_cd = #{order.matOdDeCd};
	
	
	DELETE FROM mat_order
	WHERE mat_od_cd IN (
						SELECT a.mat_od_cd
						FROM mat_order a LEFT JOIN mat_order_de b
						ON(a.mat_od_cd = b.mat_od_cd)
						WHERE mat_od_de_cd IS NULL);
END;
