mapper.select:
SELECT a.mat_lot mat_lot, a.mat_code mat_code, b.mat_name mat_name, c.act_name act_name, a.mat_out_amt mat_out_amt, a.prcs_dir_de_code prcs_dir_de_code, d.emp_name emp_name, a.mat_outd mat_outd, a.mat_exd mat_exd 
FROM mat_out a JOIN com_mat b
	ON(a.mat_code = b.mat_code)
		JOIN com_act c
			ON(a.act_code = c.act_code)
				JOIN com_emp d
					ON(a.emp_code = d.emp_code)

	
		AND a.mat_code = #{materialCode}
	
	
		AND a.act_code = #{accountCode}
	
	
		AND a.mat_outd >= #{startDate}
	
	
		AND a.mat_outd <= #{endDate}
	

ORDER BY a.mat_outd DESC
