mapper.insert:
			INSERT INTO mat_rt (
                MAT_RT_CODE, MAT_TEST_CODE, ACT_CODE,
                MAT_CODE, MAT_RT_AMT, MAT_RT_DATE,
                MAT_RT_STS, EMP_CODE
            ) VALUES (
                get_primaryCode('matRtCode'),

                #{matTestCode}, #{actCode},
                #{matCode}, #{matRtAmt}, #{matRtDate},
                #{matRtSts}, #{empCode}
            )

mapper.update:
		 UPDATE mat_test
            SET err_rt_sts = 'Y'
            WHERE mat_test_code = #{matTestCode}


	UPDATE mat_rt
	SET mat_rt_amt = #{matRtAmt}, mat_rt_date = #{matRtDate}, mat_rt_sts = #{matRtSts}
	WHERE mat_rt_code = #{matRtCode}

UPDATE mat_test
SET err_rt_sts = 'N'
WHERE mat_test_code = #{matTestCode}
