mapper.select:
SELECT a.semi_lot , semi_use_yn(semi_lot) use_yn , a.prod_code , b.prod_name , a.semi_in_amt , a.emp_code , a.prcs_ing_code , c.emp_name , a.semi_ind , a.semi_exd FROM semi_in a JOIN com_prod b ON(a.prod_code = b.prod_code) LEFT JOIN com_emp c ON(a.emp_code = c.emp_code)
AND a.prod_code LIKE '%' || #{productCode} || '%'
AND TO_CHAR(a.semi_ind, 'YYYY-MM-DD') >= #{startDate}
AND TO_CHAR(a.semi_ind, 'YYYY-MM-DD') <= #{endDate}
and semi_use_yn(semi_lot) in
#{item}
ORDER BY a.semi_ind

mapper.insert:
INSERT INTO semi_in (
	SEMI_LOT
	, PROD_CODE
	, SEMI_IN_AMT
	, SEMI_EXD
	, SEMI_IND
	, EMP_CODE
	, PRCS_ING_CODE
) VALUES (
	get_primaryCode('semiLot')
	, #{prodCode}
	, #{semiInAmt}
	, #{semiExd}
	, #{semiInd}
	, #{empCode}
	, #{prcsIngCode}
)

mapper.update:
UPDATE prcs_ing
SET prcs_dir_ing_sts = 'S5'
WHERE prcs_ing_code = #{prcsIngCode}

UPDATE semi_in
SET semi_ind = #{semiInd}, semi_exd = #{semiExd}
WHERE semi_lot = #{semiLot}
