mapper.select:
SELECT a.semi_lot semi_lot, semi_use_yn(semi_lot) use_yn, a.prod_code prod_code, b.prod_name prod_name, a.semi_in_amt semi_in_amt, a.emp_code, a.prcs_list_code, c.emp_name emp_name, a.semi_ind semi_ind, a.semi_exd semi_exd FROM semi_in a JOIN com_prod b ON(a.prod_code = b.prod_code) LEFT JOIN com_emp c ON(a.emp_code = c.emp_code)
AND a.prod_code LIKE '%' || #{productCode} || '%'
AND TO_CHAR(a.semi_ind, 'YYYY-MM-DD') >= #{startDate}
AND TO_CHAR(a.semi_ind, 'YYYY-MM-DD') <= #{endDate}
ORDER BY a.semi_ind DESC

mapper.insert:
INSERT INTO semi_in (
	SEMI_LOT,
	PROD_CODE,
	SEMI_IN_AMT,
	SEMI_EXD,
	SEMI_IND,
	EMP_CODE,
	PRCS_LIST_CODE
) VALUES (
	get_primaryCode('semiLot')
	, #{prodCode}
	, #{semiInAmt}
	, #{semiExd}
	, #{semiInd}
	, #{empCode}
	, #{prcsListCode}
)

mapper.update:
UPDATE semi_in
SET semi_ind = #{semiInd}, semi_exd = #{semiExd}
WHERE semi_lot = #{semiLot}
