mapper.update:
INSERT INTO semi_in (
	SEMI_LOT,
	PROD_CODE,
	SEMI_IN_AMT,
	SEMI_EXD,
	SEMI_IND,
	EMP_CODE,
	PRCS_LIST_CODE
) VALUES (
	get_primaryCode('semiLot')
	, #{prodCode}
	, #{semiInAmt}
	, #{semiExd}
	, #{semiInd}
	, #{empCode}
	, #{prcsListCode}
)

UPDATE semi_in
SET semi_ind = #{semiInd}, semi_exd = #{semiExd}
WHERE semi_lot = #{semiLot}
