mapper.select:
SELECT a.semi_lot semi_lot, a.prod_code prod_code, b.prod_name prod_name, a.semi_out_amt semi_out_amt, a.prcs_list_code prcs_list_code, c.emp_name emp_name, a.semi_out_date semi_out_date, a.semi_exd semi_exd 
FROM semi_out a JOIN com_prod b
	ON(a.prod_code = b.prod_code)
		JOIN com_emp c
			ON(a.emp_code = c.emp_code)

	
		AND a.prod_code = #{productCode}
	
	
		AND a.semi_outd >= #{startDate}
	
	
		AND a.semi_outd <= #{endDate}
	

ORDER BY a.semi_outd DESC
