mapper.insert:

	SELECT 'DIR-' || TO_CHAR(sysdate, 'rrMMdd') || LPAD(NVL(MAX(SUBSTR(prcs_dir_code, -3)),0) + 1, 3, '0')
	FROM prcs_dir
	WHERE SUBSTR(prcs_dir_code, 5, 6) = TO_CHAR(sysdate, 'rrMMdd')

INSERT INTO prcs_dir (
						prcs_dir_code
						, prcs_plan_code
						, prcs_dir_name
						, prcs_start_date
						
						, prcs_end_date
						
						, emp_code
					  )
	 VALUES (
				#{prcsDirCode}
				, #{prcsPlanCode}
				, #{prcsDirName}
				, #{prcsStartDate}
				
				, #{prcsEndDate}
				
				, #{empCode}
			 )


	SELECT 'DIRDE-' || TO_CHAR(sysdate, 'rrMMdd') || LPAD(NVL(MAX(SUBSTR(prcs_dir_de_code, -3)),0) + 1, 3, '0')
	FROM prcs_dir_de
	WHERE SUBSTR(prcs_dir_de_code, 7, 6) = TO_CHAR(sysdate, 'rrMMdd')

INSERT INTO prcs_dir_de (
						prcs_dir_de_code
						, prcs_dir_code
					    , prod_code
					    
					    , prcs_plan_amt
					    
					    , prcs_dir_amt
					    , prcs_start_de_date	 
					    , emp_code
					  )
	 VALUES (
				#{prcsDirDeCode}
				, #{prcsDirCode}
				, #{prodCode}
				
				, #{prcsPlanAmt}
				
				, #{prcsDirAmt}
				, #{prcsStartDeDate}
				, #{empCode}
			 )

mapper.update:
UPDATE prcs_plan_de
   SET prcs_dir_sts = 'T2'
 WHERE prcs_plan_de_code = #{prcsPlanDeCode}
