mapper.select:
		SELECT eq_code,
			   find_eq_name(eq_code) AS eq_name
		  FROM prcs_list
		 WHERE prcs_dir_de_code = #{prcsDirDeCode}
    		   AND prcs_code = #{prcsCode}

SELECT l.prcs_list_code, l.prcs_dir_de_code, find_prod_name(l.prod_code) AS prod_name, find_prcs_name(l.prcs_code) AS prcs_name, find_eq_name(l.eq_code) AS eq_name, l.input_amt, l.err_amt, l.prcs_amt, l.prcs_start_time, l.prcs_end_time, l.emp_code, d.prcs_dir_date FROM prcs_list l JOIN prcs_dir_de dd ON l.prcs_dir_de_code = dd.prcs_dir_de_code JOIN prcs_dir d ON dd.prcs_dir_code = d.prcs_dir_code
AND l.prod_code LIKE '%' || #{searchProdCode} || '%'
AND TO_CHAR(d.prcs_dir_date, 'YYYY-MM-DD') >= #{startDate}
AND TO_CHAR(d.prcs_dir_date, 'YYYY-MM-DD') <= #{endDate}
ORDER BY prcs_list_code
