mapper.insert:

	SELECT 'PRCS-' || LPAD((NVL(MAX(SUBSTR(prcs_code, -4)),0)+1),4,'0')
	FROM prcs_manage

INSERT INTO prcs_manage (
							prcs_code
							, prcs_type
							, prcs_name
							, semi_yn
					 	)
	 VALUES (
				#{prcsCode}
				, #{prcsType}
				, #{prcsName}
				, #{semiYn}
			 )

mapper.update:
	UPDATE prcs_manage 
	
		
			prcs_type = #{prcsType},
		
		
			prcs_name = #{prcsName},
		
		
			semi_yn = #{semiYn}
		
	 
  	 WHERE prcs_code = #{prcsCode}
