mapper.select:
SELECT prcs_plan_code, prcs_plan_name, prcs_plan_date, emp_code, find_emp_name(emp_code) AS emp_name, find_cdnm('0V', prcs_dir_yn) AS prcs_dir_yn, prcs_start_date, prcs_end_date FROM prcs_plan
AND prcs_plan_name LIKE '%' || #{searchPlanName} || '%'
AND TO_CHAR(prcs_plan_date, 'YYYY-MM-DD') >= #{startDate}
AND TO_CHAR(prcs_plan_date, 'YYYY-MM-DD') <= #{endDate}
ORDER BY prcs_plan_code DESC

mapper.insert:

	SELECT 'PLAN-' || TO_CHAR(sysdate, 'rrMMdd') || LPAD(NVL(MAX(SUBSTR(prcs_plan_code, -3)),0) + 1, 3, '0')
	FROM prcs_plan
	WHERE SUBSTR(prcs_plan_code, 6, 6) = TO_CHAR(sysdate, 'rrMMdd')

INSERT INTO prcs_plan (
							prcs_plan_code
							, prcs_plan_name
							, emp_code
							, prcs_start_date
							, prcs_end_date
					   )
		 VALUES (
					#{prcsPlanCode}
					, #{prcsPlanName}
					, #{empCode}
					, #{prcsStartDate}
					, #{prcsEndDate}
				)

	
		SELECT 'PLANDE-' || TO_CHAR(sysdate, 'rrMMdd') || LPAD(NVL(MAX(SUBSTR(prcs_plan_de_code, -3)),0) + 1, 3, '0')
		FROM prcs_plan_de
		WHERE SUBSTR(prcs_plan_de_code, 8, 6) = TO_CHAR(sysdate, 'rrMMdd')
	
	INSERT INTO prcs_plan_de (
									prcs_plan_de_code
									, prcs_plan_code
									, prod_code
									, prcs_rq_amt
									, prcs_plan_amt
									
									, prcs_prio
									
							 )
             VALUES ( 
						#{prcsPlanDeCode}
						, #{prcsPlanCode}
						, #{prodCode}
						, #{prcsRqAmt}
						, #{prcsPlanAmt}
						
						, #{prcsPrio} 
						
					)

mapper.update:
UPDATE prcs_plan
					
	
		prcs_plan_name = #{prcsPlanName},
		
	
		emp_code = #{empCode},
	
	
		prcs_start_date = #{prcsStartDate},
	
	
		prcs_end_date = #{prcsEndDate}
	

WHERE prcs_plan_code = #{prcsPlanCode}

	UPDATE sales_ord
	   SET ord_sts = 'P2',
    	   prcs_plan_code = #{prcsPlanCode}
	 WHERE ord_code = #{ordCode}
