mapper.insert:

	SELECT 'PRPR-' || TO_CHAR(sysdate, 'rrMMdd') || LPAD(NVL(MAX(SUBSTR(prcs_prod_code, -3)),0) + 1, 3, '0')
	FROM prcs_prod
	WHERE SUBSTR(prcs_prod_code, 6, 6) = TO_CHAR(sysdate, 'rrMMdd')

INSERT INTO prcs_prod (
							prcs_prod_code
							,prod_code
							, prcs_code
							, prcs_seq
					 	)
	 VALUES (
				#{prcsProdCode}
				, #{prodCode}
				, #{prcsCode}
				, #{prcsSeq}
			 )

mapper.update:
	UPDATE prcs_prod 
	
		
			prcs_code = #{prcsCode},
		
		
			prcs_seq = #{prcsSeq}
		
	 
  	 WHERE prcs_prod_code = #{prcsProdCode}
