mapper.insert:
	  	
			 SELECT 'MAT-TEST-' || TO_CHAR(sysdate, 'rrMMdd') || LPAD(NVL(MAX(TO_NUMBER(SUBSTR(MAT_TEST_CODE, -3))), 0) + 1, 3, '0') AS NEW_TEST_CODE
			FROM MAT_TEST
			WHERE SUBSTR(MAT_TEST_CODE, 10, 6) = TO_CHAR(sysdate, 'rrMMdd')
		
       {
  		CALL INSERT_MAT_CHK(
  			#{matTestCode}
  			,#{matAmt}
  			,#{matYamt}
  			,#{matNamt}
  			,#{matTestDate}
  			,#{errCode}
  			,#{empCode}
  			,#{matOdDeCd}
  			,#{errRtSts}
  			)
  		}

mapper.update:
UPDATE MAT_TEST
SET MAT_YAMT =#{matYamt}
	,MAT_NAMT =#{matNamt}
	,MAT_TEST_DATE =#{matTestDate}
	,ERR_CODE =#{errCode}
	,EMP_CODE =#{empCode}
	,ERR_RT_STS =#{errRtSts}
WHERE MAT_TEST_CODE =#{matTestCode}
