mapper.insert:

	SELECT 'ORD-' || TO_CHAR(sysdate, 'rrMMdd') ||
	LPAD(NVL(MAX(SUBSTR(ord_code, -3)),0) + 1, 3, '0')
	FROM sales_ord
	WHERE SUBSTR(ord_code, 5, 6) = TO_CHAR(sysdate, 'rrMMdd')

INSERT INTO
sales_ord(
	ord_code
	, ord_date
	, act_code
	, emp_code
	, prcs_plan_code)
VALUES(
	#{ordCode}
	,#{ordDate}
	,#{actCode}
	,#{empCode}
	,#{prcsPlanCode}
)

mapper.update:
UPDATE sales_ord_de
SET
prcs_rq_amt = #{prcsRqAmt}
, prod_code = #{prodCode}
, dev_date = #{devDate}
WHERE sales_ord_de_code = #{salesOrdDeCode}
